const filterForm=document.querySelectorAll(".filter-form"),filterFormTitles=document.querySelectorAll(".filter-form__title");for(let e=0;e<=filterFormTitles.length;e++)filterFormTitles[e].addEventListener("click",(function(){filterForm[e].classList.contains("filter-form--collapsed")?filterForm[e].classList.remove("filter-form--collapsed"):filterForm[e].classList.add("filter-form--collapsed")}));